import React, { useState } from "react";
import axios from "axios";

const STEP = {
  DASHBOARD: "DASHBOARD",
  UPLOAD_PDF: "UPLOAD_PDF",
  EDIT_DATA: "EDIT_DATA",
  ANALYSIS_RESULT: "ANALYSIS_RESULT",
};

function App() {
  const [currentStep, setCurrentStep] = useState(STEP.DASHBOARD);
  const [file, setFile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [analysis, setAnalysis] = useState(null);

  const handleNewApplication = () => {
    setCurrentStep(STEP.UPLOAD_PDF);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUploadPdf = async () => {
    if (!file) return;
    try {
      const formData = new FormData();
      formData.append("file", file);

      const res = await axios.post("http://localhost:8000/upload-pdf", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.transactions && res.data.transactions.length > 0) {
        setTransactions(res.data.transactions);
      } else {
        alert("No transactions found. Starting with an empty table.");
        setTransactions([{ date: "", description: "", credit: 0, debit: 0, balance: 0 }]);
      }

      setCurrentStep(STEP.EDIT_DATA);
    } catch (error) {
      console.error(error);
      alert("Error uploading file. Starting with an empty table.");
      setTransactions([{ date: "", description: "", credit: 0, debit: 0, balance: 0 }]);
      setCurrentStep(STEP.EDIT_DATA);
    }
  };

  const handleTransactionChange = (index, field, value) => {
    const updatedTransactions = [...transactions];
    updatedTransactions[index][field] = value;
    setTransactions(updatedTransactions);
  };

  const handleAddRow = () => {
    setTransactions([
      ...transactions,
      { date: "", description: "", credit: 0, debit: 0, balance: 0 },
    ]);
  };

  const handleAnalyze = async () => {
    try {
      const res = await axios.post("http://localhost:8000/analyze-transactions", transactions, {
        headers: { "Content-Type": "application/json" },
      });
      if (res.data.analysis) {
        setAnalysis(res.data.analysis);
      }
      setCurrentStep(STEP.ANALYSIS_RESULT);
    } catch (error) {
      console.error(error);
      alert("Error analyzing transactions.");
    }
  };

  const renderMainContent = () => {
    switch (currentStep) {
      case STEP.DASHBOARD:
        return (
          <div>
            <h1 className="text-2xl font-bold mb-4">Dashboard</h1>
            <button
              onClick={handleNewApplication}
              className="px-4 py-2 bg-blue-600 text-white rounded"
            >
              New Application
            </button>
          </div>
        );

      case STEP.UPLOAD_PDF:
        return (
          <div>
            <h2 className="text-lg font-semibold">Upload Bank Statement</h2>
            <input type="file" accept=".pdf" onChange={handleFileChange} />
            <button
              onClick={handleUploadPdf}
              className="px-4 py-2 bg-blue-600 text-white rounded mt-2"
            >
              Upload
            </button>
          </div>
        );

      case STEP.EDIT_DATA:
        return (
          <div>
            <h2 className="text-lg font-semibold mb-4">Review & Edit Transactions</h2>
            <table className="min-w-full table-auto border mb-4">
              <thead>
                <tr className="bg-gray-100">
                  <th className="px-4 py-2 border">Date</th>
                  <th className="px-4 py-2 border">Description</th>
                  <th className="px-4 py-2 border">Credit</th>
                  <th className="px-4 py-2 border">Debit</th>
                  <th className="px-4 py-2 border">Balance</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map((txn, idx) => (
                  <tr key={idx}>
                    <td className="px-4 py-2 border">
                      <input
                        type="text"
                        className="w-full"
                        value={txn.date}
                        onChange={(e) =>
                          handleTransactionChange(idx, "date", e.target.value)
                        }
                      />
                    </td>
                    <td className="px-4 py-2 border">
                      <input
                        type="text"
                        className="w-full"
                        value={txn.description}
                        onChange={(e) =>
                          handleTransactionChange(idx, "description", e.target.value)
                        }
                      />
                    </td>
                    <td className="px-4 py-2 border">
                      <input
                        type="number"
                        className="w-full"
                        value={txn.credit}
                        onChange={(e) =>
                          handleTransactionChange(idx, "credit", parseFloat(e.target.value || 0))
                        }
                      />
                    </td>
                    <td className="px-4 py-2 border">
                      <input
                        type="number"
                        className="w-full"
                        value={txn.debit}
                        onChange={(e) =>
                          handleTransactionChange(idx, "debit", parseFloat(e.target.value || 0))
                        }
                      />
                    </td>
                    <td className="px-4 py-2 border">
                      <input
                        type="number"
                        className="w-full"
                        value={txn.balance}
                        onChange={(e) =>
                          handleTransactionChange(idx, "balance", parseFloat(e.target.value || 0))
                        }
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button
              onClick={handleAddRow}
              className="px-4 py-2 bg-green-600 text-white rounded mb-4"
            >
              Add Row
            </button>
            <button
              onClick={handleAnalyze}
              className="px-4 py-2 bg-blue-600 text-white rounded"
            >
              Submit for Analysis
            </button>
          </div>
        );

      case STEP.ANALYSIS_RESULT:
        return (
          <div>
            <h2 className="text-lg font-semibold mb-4">Loan Analysis Result</h2>
            {analysis ? (
              <div>
                <p><strong>Total Income:</strong> {analysis.total_income}</p>
                <p><strong>Total Expenses:</strong> {analysis.total_expenses}</p>
                <p><strong>Net Flow:</strong> {analysis.net_flow}</p>
                <p><strong>Loan Decision:</strong> {analysis.recommendation}</p>
              </div>
            ) : (
              <p>No analysis data available.</p>
            )}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="h-screen flex flex-col">
      <header className="bg-gray-100 p-4 border-b">
        <h1 className="text-xl font-bold">Bank Loan System</h1>
      </header>
      <main className="flex-1 p-6">{renderMainContent()}</main>
    </div>
  );
}

export default App;
