import React, { useState } from 'react';
import axios from 'axios';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

/**
 * Steps in the loan application workflow:
 *  - "DASHBOARD": The default dashboard view
 *  - "UPLOAD_PDF": A screen for uploading the PDF statement
 *  - "EDIT_DATA": A screen displaying the parsed transactions in an editable table
 *  - "ANALYSIS_RESULT": A screen showing the final loan analysis & statistics
 */
const STEP = {
  DASHBOARD: 'DASHBOARD',
  UPLOAD_PDF: 'UPLOAD_PDF',
  EDIT_DATA: 'EDIT_DATA',
  ANALYSIS_RESULT: 'ANALYSIS_RESULT'
};

function App() {
  // State to track the user's current workflow step
  const [currentStep, setCurrentStep] = useState(STEP.DASHBOARD);

  // State for PDF upload
  const [file, setFile] = useState(null);

  // State for storing transactions from the server
  const [transactions, setTransactions] = useState([]);

  // State for analysis result
  const [analysis, setAnalysis] = useState(null);

  // Sample data for "monthly income" and "monthly expenses" charts on the dashboard
  const monthlyIncomeData = [
    { month: 'Jan', amount: 3000 },
    { month: 'Feb', amount: 3500 },
    { month: 'Mar', amount: 4000 },
    { month: 'Apr', amount: 4500 },
  ];

  const monthlyExpenseData = [
    { month: 'Jan', amount: 2000 },
    { month: 'Feb', amount: 1800 },
    { month: 'Mar', amount: 2100 },
    { month: 'Apr', amount: 2200 },
  ];

  /**
   * Handle user clicking on "New Application" to start the workflow
   */
  const handleNewApplication = () => {
    setCurrentStep(STEP.UPLOAD_PDF);
  };

  /**
   * Handle PDF file selection
   */
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  /**
   * Step 1 & 2: User uploads PDF â†’ Back-end returns parsed transactions
   */
  const handleUploadPdf = async () => {
    if (!file) return;
    try {
      const formData = new FormData();
      formData.append('file', file);

      // POST to your /upload-pdf endpoint
      const res = await axios.post('http://localhost:8000/upload-pdf', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      console.log('Server response:', res.data);

      if (res.data.transactions) {
        setTransactions(res.data.transactions);
        // Go to the next step: show the user an editable table
        setCurrentStep(STEP.EDIT_DATA);
      }
    } catch (error) {
      console.error(error);
      alert('Error uploading file.');
    }
  };

  /**
   * Handle changes in the editable transactions table
   */
  const handleTransactionChange = (index, field, value) => {
    const newTransactions = [...transactions];
    newTransactions[index][field] = value;
    setTransactions(newTransactions);
  };

  /**
   * Step 5: Submit the user-reviewed transactions for analysis
   */
  const handleAnalyze = async () => {
    try {
      // POST to /analyze-transactions
      const res = await axios.post('http://localhost:8000/analyze-transactions', transactions, {
        headers: { 'Content-Type': 'application/json' },
      });
      console.log('Analysis response:', res.data);
      if (res.data.analysis) {
        setAnalysis(res.data.analysis);
      } else {
        // fallback if structure differs
        setAnalysis(res.data);
      }

      // Move to final step
      setCurrentStep(STEP.ANALYSIS_RESULT);
    } catch (error) {
      console.error(error);
      alert('Error analyzing transactions.');
    }
  };

  /**
   * Step 6 & 7: We show the final analysis (including the loan decision) plus stats.
   */

  // ----------- RENDERING THE MAIN CONTENT BASED ON currentStep ----------- //

  const renderMainContent = () => {
    switch (currentStep) {
      case STEP.DASHBOARD:
        // Default bank dashboard with monthly charts and decoy placeholders
        return (
          <div>
            <h1 className="text-2xl font-bold mb-4">Dashboard</h1>

            {/* Analytics Section */}
            <div className="flex flex-col lg:flex-row gap-4 mb-4">
              {/* Monthly Income Chart */}
              <div className="bg-white shadow p-4 flex-1">
                <h2 className="text-sm font-semibold mb-2">Average Monthly Income</h2>
                <div style={{ width: '100%', height: 200 }}>
                  <ResponsiveContainer>
                    <LineChart data={monthlyIncomeData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="month" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Line type="monotone" dataKey="amount" stroke="#8884d8" />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              </div>

              {/* Monthly Expenses Chart */}
              <div className="bg-white shadow p-4 flex-1">
                <h2 className="text-sm font-semibold mb-2">Average Monthly Expenses</h2>
                <div style={{ width: '100%', height: 200 }}>
                  <ResponsiveContainer>
                    <LineChart data={monthlyExpenseData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="month" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Line type="monotone" dataKey="amount" stroke="#82ca9d" />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              </div>
            </div>

            {/* Decoy: some recent bank statements or recent loan applications, etc. */}
            <div className="mb-4">
              <h2 className="text-lg font-semibold mb-2">Recent Loan Applications</h2>
              <div className="bg-white shadow p-4 mb-2 flex items-center justify-between">
                <div>
                  <div className="font-bold">Application #1234</div>
                  <div className="text-sm text-gray-500">Date: 2024-06-10</div>
                </div>
                <span className="inline-block px-2 py-1 text-sm bg-yellow-100 text-yellow-800 rounded">
                  Pending
                </span>
              </div>
              <div className="bg-white shadow p-4 mb-2 flex items-center justify-between">
                <div>
                  <div className="font-bold">Application #5678</div>
                  <div className="text-sm text-gray-500">Date: 2024-05-21</div>
                </div>
                <span className="inline-block px-2 py-1 text-sm bg-green-100 text-green-800 rounded">
                  Approved
                </span>
              </div>
            </div>
          </div>
        );

      case STEP.UPLOAD_PDF:
        // Step 2: Let user upload PDF
        return (
          <div className="bg-white shadow p-4">
            <h2 className="text-lg font-semibold mb-2">New Loan Application - Upload Bank Statement</h2>
            <input
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
              className="block mb-2"
            />
            <button
              onClick={handleUploadPdf}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Upload & Parse
            </button>
          </div>
        );

      case STEP.EDIT_DATA:
        // Step 4: Show user the parsed transactions in an editable table
        return (
          <div className="bg-white shadow p-4">
            <h2 className="text-lg font-semibold mb-2">Review & Edit Transactions</h2>
            {transactions.length > 0 ? (
              <table className="min-w-full table-auto border mb-4">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="px-4 py-2 border">Date</th>
                    <th className="px-4 py-2 border">Description</th>
                    <th className="px-4 py-2 border">Credit</th>
                    <th className="px-4 py-2 border">Debit</th>
                    <th className="px-4 py-2 border">Balance</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map((txn, idx) => (
                    <tr key={idx}>
                      <td className="px-4 py-2 border">
                        <input
                          type="text"
                          className="w-full"
                          value={txn.date}
                          onChange={(e) => handleTransactionChange(idx, 'date', e.target.value)}
                        />
                      </td>
                      <td className="px-4 py-2 border">
                        <input
                          type="text"
                          className="w-full"
                          value={txn.description}
                          onChange={(e) => handleTransactionChange(idx, 'description', e.target.value)}
                        />
                      </td>
                      <td className="px-4 py-2 border">
                        <input
                          type="number"
                          step="0.01"
                          className="w-full"
                          value={txn.credit}
                          onChange={(e) =>
                            handleTransactionChange(idx, 'credit', parseFloat(e.target.value || 0))
                          }
                        />
                      </td>
                      <td className="px-4 py-2 border">
                        <input
                          type="number"
                          step="0.01"
                          className="w-full"
                          value={txn.debit}
                          onChange={(e) =>
                            handleTransactionChange(idx, 'debit', parseFloat(e.target.value || 0))
                          }
                        />
                      </td>
                      <td className="px-4 py-2 border">
                        <input
                          type="number"
                          step="0.01"
                          className="w-full"
                          value={txn.balance}
                          onChange={(e) =>
                            handleTransactionChange(idx, 'balance', parseFloat(e.target.value || 0))
                          }
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No transactions found.</p>
            )}
            <button
              onClick={handleAnalyze}
              className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
            >
              Submit for Analysis
            </button>
          </div>
        );

      case STEP.ANALYSIS_RESULT:
        // Step 6 & 7: Show the final analysis, loan decision, and stats
        return (
          <div className="bg-white shadow p-4">
            <h2 className="text-lg font-semibold mb-2">Loan Analysis Result</h2>
            {analysis ? (
              <div>
                <p><strong>Total Income:</strong> {analysis.total_income}</p>
                <p><strong>Total Expenses:</strong> {analysis.total_expenses}</p>
                <p><strong>Net Flow:</strong> {analysis.net_flow}</p>
                <p className="mt-2"><strong>Loan Decision:</strong> {analysis.recommendation}</p>
              </div>
            ) : (
              <p>No analysis data available.</p>
            )}
            <button
              onClick={() => setCurrentStep(STEP.DASHBOARD)}
              className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Go Back to Dashboard
            </button>
          </div>
        );

      default:
        return <div>Unknown step</div>;
    }
  };

  // -------------- MAIN RETURN --------------

  return (
    <div className="h-screen flex flex-col bg-gray-100">
      {/* Top Navbar */}
      <nav className="flex items-center justify-between bg-white px-4 py-2 border-b">
        <div className="flex items-center space-x-4">
          <div className="font-bold text-xl">Bankly Loan System</div>
          <button
            className="px-3 py-1 text-sm bg-white border rounded hidden md:block"
            onClick={() => setCurrentStep(STEP.DASHBOARD)}
          >
            Dashboard
          </button>
          <button
            className="px-3 py-1 text-sm bg-white border rounded hidden md:block"
            onClick={handleNewApplication}
          >
            New Application
          </button>
        </div>
        <div className="flex items-center space-x-4">
          <input
            className="border rounded px-2 py-1 text-sm"
            placeholder="Search..."
          />
          <button className="text-gray-600">ðŸ””</button>
          <button className="bg-gray-200 rounded-full h-8 w-8 flex items-center justify-center text-gray-700">
            <span className="text-sm">U</span>
          </button>
        </div>
      </nav>

      <div className="flex flex-1 overflow-hidden">
        {/* Sidebar (Decoy) */}
        <aside className="w-64 bg-white border-r p-4 overflow-y-auto hidden md:block">
          <nav className="space-y-2">
            <h3 className="text-lg font-semibold mb-2">Loans</h3>
            <ul className="space-y-1 text-sm">
              <li><button className="text-gray-600 hover:text-black">Pending Applications</button></li>
              <li><button className="text-gray-600 hover:text-black">Approved Loans</button></li>
              <li><button className="text-gray-600 hover:text-black">Denied Loans</button></li>
              <li><button className="text-gray-600 hover:text-black">Archived Loans</button></li>
            </ul>

            <h3 className="text-lg font-semibold mt-6 mb-2">Customers</h3>
            <ul className="space-y-1 text-sm">
              <li><button className="text-gray-600 hover:text-black">Customer Directory</button></li>
              <li><button className="text-gray-600 hover:text-black">Loan History by Customer</button></li>
            </ul>

            <h3 className="text-lg font-semibold mt-6 mb-2">Reports</h3>
            <ul className="space-y-1 text-sm">
              <li><button className="text-gray-600 hover:text-black">Loan Trends</button></li>
              <li><button className="text-gray-600 hover:text-black">Approval Rates</button></li>
              <li><button className="text-gray-600 hover:text-black">Risk Metrics</button></li>
            </ul>

            <h3 className="text-lg font-semibold mt-6 mb-2">Settings</h3>
            <ul className="space-y-1 text-sm">
              <li><button className="text-gray-600 hover:text-black">User Preferences</button></li>
              <li><button className="text-gray-600 hover:text-black">Sign Out</button></li>
            </ul>
          </nav>
        </aside>

        {/* Main Content - Render conditionally based on the step */}
        <main className="flex-1 overflow-y-auto p-6">
          {renderMainContent()}
        </main>
      </div>
    </div>
  );
}

export default App;
